    public List<BillCreatedEventResponse> getBillCreatedEvents(
            TransactionReceipt transactionReceipt) {
        List<Contract.EventValuesWithLog> valueList = extractEventParametersWithLog(BILLCREATED_EVENT, transactionReceipt);
        ArrayList<BillCreatedEventResponse> responses = new ArrayList<BillCreatedEventResponse>(valueList.size());
        for (Contract.EventValuesWithLog eventValues : valueList) {
            BillCreatedEventResponse typedResponse = new BillCreatedEventResponse();
            typedResponse.log = eventValues.getLog();
            typedResponse.billId = (byte[]) eventValues.getIndexedValues().get(0).getValue();
            typedResponse.billNo = (String) eventValues.getNonIndexedValues().get(0).getValue();
            typedResponse.amount = (BigInteger) eventValues.getNonIndexedValues().get(1).getValue();
            responses.add(typedResponse);
        }
        return responses;
    }

